  if (!analyticsData) return null;

  const categoryData = Object.entries(analyticsData.categoryCount).map(
    ([name, value]) => ({
      name,
      value,
    })
  );

  const statusData = Object.entries(analyticsData.statusCount).map(
    ([name, value]) => ({
      name,
      value,
    })
